import Navigation from "../components/Navigation";
import React from "react";
import {Form, Button, Row, Col} from "react-bootstrap";

export default function Tables() {
  const [formData, setFormData] = React.useState(
      {
        tableType: "",
        tableName: "",
      }
    )

  const [columnFields, setColumnFields] = React.useState([])

  function handleChange(event) {
    const {name, value, type, checked} = event.target
    setFormData(prevFormData => {
        return {
            ...prevFormData,
            [name]: value
        }
    })
  }

  const handleColumnChange = (index, event) => {
    let data = [...columnFields]
    data[index][event.target.name] = event.target.value
    setColumnFields(data);
  }

  const handleAddColumnClick = () => {
    let newcolumn = { columnName: '', columnType: '' }
    setColumnFields([...columnFields, newcolumn])
  };

  const removeColumns = (index) => {
    let data = [...columnFields]
    data.splice(index, 1)
    setColumnFields(data)
  }

  const handleResult = () => {
    let firstColumn = columnFields.length >= 1 ? columnFields[0].columnName : ""
    if (formData.tableType === 'create') {
      let result = `rails generate migration ${formData.tableName}`
      return result
    } else if (columnFields.length >= 1 && formData.tableType === 'add-column') {
      columnFields.length = 1
      let result = `rails generate migration Add${firstColumn}To${formData.tableName}`
      return result
    } else if (formData.tableType === 'create-join') {
      let result = `rails generate create-join ${formData.tableName}`
      return result
    } else if (formData.tableType === 'remove-column') {
      columnFields.length = 1
      let result = `rails generate Remove${firstColumn}From${formData.tableName}`
      return result
    } else if (formData.tableType === 'create-table-and-model') {
      let result = `rails generate model ${formData.tableName}`
      return result
    } else {
      return ""
    }
  }

  const handleColumns = () => {
    let fields = []
    // const camelToSnakeCase = str => str.replace(/[A-Z]/g, letter => `_${letter.toLowerCase()}`);
    function cleanUpResult(res) {
      let newFields = res.toString().split(',').join(' ')
      let result = newFields.split(/(?=[A-Z])/).join('_').toLowerCase();
    }

    const isColumn = columnFields.some(element => {
      if (element.columnName != '' && element.columnType != '') {
        return true;
      }
      return false;
    });

    const semiColon = isColumn ? ":" : ""

    if (isColumn && 
      formData.tableType === 'create' ||
      formData.tableType === 'create-table-and-model') {
      for (var i=0; i<columnFields.length; i++) {
        fields.push(columnFields[i].columnName + semiColon + columnFields[i].columnType)
      }
      cleanUpResult(fields)
      return result

    } else if (isColumn && 
      formData.tableType === 'add-column' ||
      formData.tableType === 'remove-column') {
        fields.push(columnFields[0].columnName + semiColon + columnFields[0].columnType)
        let result = camelToSnakeCase(fields.toString())
        return result

    } else {
      return ""
    }
  }

  const result = handleResult() + " " + handleColumns()

  return (
    <div>
      <Navigation />
      <div className="main">
        <h2>Tables</h2>

        <Form.Select aria-label="Default select example"
          id="tableType" 
          value={formData.tableType}
          onChange={handleChange}
          name="tableType"
        >
          <option value="">-- Select --</option>
          <option value="create">Create Table</option>
          <option value="add-column">Add Column to Existing Table</option>
          <option value="remove-column">Remove Column from Existing Table</option>
          <option value="create-table-and-model">Create a table with a corresponding model</option>
          <option value="create-join-table">Create a join table</option>
          <option value="create-controller">Create a controller</option>
          <option value="create-model-belongs-to">Create a model with belongs_to</option>
          <option value="create-scaffold">Create a scaffold (Controller, Model, View, Route, and Migration)</option>
        </Form.Select>

        {formData.tableType !== '' && 
        <div className="form-group">
          <Form.Label>Table Name (In CamelCase)</Form.Label>
          <Form.Control
            onChange={handleChange}
            value={formData.tableName}
            name="tableName"
            placeholder="Table Name"
          />
        </div>
        }

        {/* -------------------------------------- COLUMN FORM -------------------------------- */}

        <Button 
          id="form-submit" 
          variant="primary"
          onClick={handleAddColumnClick}>
            Add Column
        </Button>{' '}
        <br/>

        {columnFields.map((input, index) => {
          return (
          <div className="column" key={index}>
          <Form.Label>Column Name (In CamelCase)</Form.Label>
          <br/>
          <Form>
            <div>
              <Row>
                <Col>
                  <Form.Control
                    name='columnName'
                    placeholder="Column Name"
                    className={index}
                    value={input.columnName}
                    onChange={event => handleColumnChange(index, event)}
                  />
                </Col>
                <Col>
                  <Form.Select aria-label="Default select example"
                    id="columnType" 
                    name="columnType"
                    value={input.columnType}
                    onChange={event => handleColumnChange(index, event)}
                  >
                    <option value="">-- Select --</option>
                    <option value="string">string</option>
                    <option value="text">text</option>
                    <option value="integer">integer</option>
                    <option value="boolean">boolean</option>
                    <option value="bigint">bigint</option>
                    <option value="float">float</option>
                    <option value="decimal">decimal</option>
                    <option value="numeric">numeric</option>
                    <option value="time">time</option>
                    <option value="date">date</option>
                    <option value="datetime">DateTime</option>
                    <option value="timestamp">timestamp</option>
                    <option value="binary">binary</option>
                    <option value="primary_key">primary_key</option>

                  </Form.Select>
                </Col>
              </Row>
            </div>
            <Button 
              onClick={() => removeColumns(index)}
              variant="danger"
              >
                Remove
            </Button>
          </Form>

          </div>
          )
        })}

        <br/>
        <br/>
        <br/>
        {result}
      </div>
    </div>
  );
}
