import Navigation from "../components/Navigation";
import React from "react";
import {Form} from "react-bootstrap";

export default function Tables() {
  const [formData, setFormData] = React.useState({favColor: ""})

  function handleDropDownChange(event) {
    const {name, value, type, checked} = event.target
    setFormData(prevFormData => {
        return {
            ...prevFormData,
            [name]: value
        }
    })
  }

  return (
    <div>
      <Navigation />
      <div className="main">
      <h2>Tables</h2>

      <Form.Select aria-label="Default select example"
        id="favColor" 
        value={formData.favColor}
        onChange={handleDropDownChange}
        name="favColor"
      >
        <option value="red">Create Table</option>
        <option value="orange">Add Column to current table</option>
        <option value="yellow">Remove Table</option>
        <option value="green">Create Join Table</option>
      </Form.Select>
      </div>

    </div>
  );
}
